import streamlit as st 
st.title('Coffee taste poll')

col1, col2=st.columns(2)

with col1:
    st.header('Cappucino')
    st.image('https://images.pexels.com/photos/312418/pexels-photo-312418.jpeg?cs=srgb&dl=pexels-chevanon-312418.jpg&fm=jpg', width=400)
    cap= st.button('Vote cappucino')
   

with col2:
    st.header('Americano')
    st.image('data:image/jpeg;base64,', width=400)
    ame= st.button('Vote americano')
    
if cap:
    st.success('Thank you for voting capuccion.')
elif ame:
    st.success('Thank yoy for voting americano.')
else:
    st.success('Thanks guys.')



#Sidebar

name=st.sidebar.text_input("Enter your name: ")
coffee= st.sidebar.selectbox("Choose your coffee: ", ['Cappucino','Americano','Black coffee'])
st.write(f'Here you go {name}. Your {coffee}')


with st.expander('Show coffee making instructions'):
    st.write("""
             1. Boil milk
             2. Add sugar
             3. Add coffee
             4. Put it on cup
             5. Serve it
             """)
    
    
    
#Markdown

st.markdown("### Coffee Making App")
